mapper.select:
SELECT product.content AS productContent, file_info.save_file_name AS productImageUrl, display_info.place_name AS placeName, product.description AS productDescription, product.id AS productId, display_info.id AS displayInfoId FROM product INNER JOIN display_info ON product.id = display_info.product_id INNER JOIN product_image ON product.id = product_image.product_id INNER JOIN file_info ON product_image.file_id = file_info.id WHERE product_image.type = 'th'
AND product.category_id = #{id}
LIMIT #{start}, #{limit}
